import React, { useRef, useState, useEffect } from 'react';
import './App.css';
import chatbotlogo from './assets/chat.jpg';
import addBtn from './assets/addBtn.png';
import sendBtn from './assets/send.svg';
import usericon from './assets/usericon.webp';
import c2clogo from './assets/chat.jpg';
import { sendMsgToGroq } from './api';

function App() {
  const msgEnd = useRef(null);
  const [Input, setInput] = useState("");
  const [messages, setMessages] = useState([
    {
      text: "Hi, I am Chatbot, a state-of-the-art language model. I am designed to understand and generate human-like text based on truth.",
      isBot: true,
    }
  ]);
  const [fileToSend, setFileToSend] = useState(null); // store file info here

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Function to read file content as text (for .txt only here, extend for pdf/docx in backend)
  const readFileContent = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (e) => resolve(e.target.result);
      reader.onerror = (e) => reject(e);
      reader.readAsText(file);
    });
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setFileToSend(file);

    // Show user message with file name and optional text "Sending file: ..."
    setMessages(prev => [
      ...prev,
      { text: `Sent a file: ${file.name}`, isBot: false }
    ]);

    // Read file content and send to backend
    try {
      const content = await readFileContent(file);

      // Send the file content to backend to extract questions & answer
      const botResponse = await sendMsgToGroq(content);

      setMessages(prev => [...prev, { text: botResponse, isBot: true }]);
    } catch (err) {
      setMessages(prev => [...prev, { text: "Failed to read the file.", isBot: true }]);
    }
  };

  const handleSend = async () => {
    if (Input.trim() === "") return;

    const text = Input;
    setInput('');
    setMessages(prev => [...prev, { text, isBot: false }]);

    const res = await sendMsgToGroq(text);
    setMessages(prev => [...prev, { text: res, isBot: true }]);
  };

  const handleEnter = async (e) => {
    if (e.key === 'Enter') await handleSend();
  };

  return (
    <div className="App">
      <div className="sidebar">
        {/* Your sidebar code here */}
      </div>

      <div className="main">
        <div className="chats">
          {messages.map((message, i) => (
            <div key={i} className={`chat ${message.isBot ? "left" : "right"}`}>
              <img
                src={message.isBot ? c2clogo : usericon}
                alt={message.isBot ? "Bot" : "User"}
                className="chatIcon"
              />
              <p className="txt">{message.text}</p>
            </div>
          ))}
          <div ref={msgEnd} />
        </div>

        <div className="chatFooter">
          <label htmlFor="file-upload" className="uploadBtn" title="Upload file">
            ðŸ“Ž
          </label>
          <input
            id="file-upload"
            type="file"
            accept=".txt,.pdf,.docx"
            style={{ display: "none" }}
            onChange={handleFileChange}
          />

          <div className="inp">
            <input
              type="text"
              placeholder="Send a Message"
              value={Input}
              onKeyDown={handleEnter}
              onChange={(e) => setInput(e.target.value)}
            />
            <button className="send" onClick={handleSend}>
              <img src={sendBtn} alt="Send" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
