import './App.css';
import chatbotlogo from './assets/chat.jpg';
import addBtn from './assets/addBtn.png';
import sendBtn from './assets/send.svg';
import usericon from './assets/usericon.webp';
import c2clogo from './assets/chat.jpg';
import voiceIcon from './assets/voiceIcon.png';
import stopIcon from './assets/stop.png';

import { sendMsgToGroq } from './api';
import { useRef, useState, useEffect } from 'react';

import mammoth from 'mammoth';
import * as pdfjsLib from 'pdfjs-dist';
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

const extractQuestionsFromText = (text) => {
  const questionRegex = /[^.?!]*\?/g;
  return text.match(questionRegex) || [];
};

const readFileContent = async (file) => {
  const fileType = file.name.split('.').pop().toLowerCase();

  if (fileType === 'txt') {
    return await file.text();
  } else if (fileType === 'docx') {
    const arrayBuffer = await file.arrayBuffer();
    const result = await mammoth.extractRawText({ arrayBuffer });
    return result.value;
  } else if (fileType === 'pdf') {
    const arrayBuffer = await file.arrayBuffer();
    const pdf = await pdfjsLib.getDocument({ data: arrayBuffer }).promise;
    let text = '';
    for (let i = 1; i <= pdf.numPages; i++) {
      const page = await pdf.getPage(i);
      const content = await page.getTextContent();
      const pageText = content.items.map(item => item.str).join(' ');
      text += pageText + '\n';
    }
    return text;
  } else {
    throw new Error('Unsupported file type');
  }
};

function App() {
  const msgEnd = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [voiceText, setVoiceText] = useState('');
  const [recognition, setRecognition] = useState(null);
  const [savedChats, setSavedChats] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const [Input, setInput] = useState("");
  const [messages, setMessages] = useState([
    {
      text: "Hi, I am Chatbot, a state-of-the-art language model. I am designed to understand and generate human-like text based on truth.",
      isBot: true,
    }
  ]);

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognizer = new SpeechRecognition();
      recognizer.continuous = false;
      recognizer.interimResults = false;
      recognizer.lang = 'en-US';

      recognizer.onresult = async (event) => {
        const transcript = event.results[0][0].transcript;
        setVoiceText(transcript);
        setMessages(prev => [...prev, { text: transcript, isBot: false }]);
        const res = await sendMsgToGroq(transcript);
        setMessages(prev => [...prev, { text: res, isBot: true }]);
      };

      setRecognition(recognizer);
    }
  }, []);

  const handleSend = async () => {
    if (Input.trim() === "") return;
    const text = Input;
    setInput('');
    setMessages(prev => [...prev, { text, isBot: false }]);
    const res = await sendMsgToGroq(text);
    setMessages(prev => [...prev, { text: res, isBot: true }]);
  };

  const handleEnter = async (e) => {
    if (e.key === 'Enter') await handleSend();
  };

  const handleSaveChat = () => {
    const currentDateTime = new Date().toLocaleString();
    let saved = JSON.parse(localStorage.getItem('savedChats'));

    if (!Array.isArray(saved)) {
      saved = [];
    }

    const newChat = {
      date: currentDateTime,
      messages: messages,
    };

    saved.push(newChat);
    localStorage.setItem('savedChats', JSON.stringify(saved));
    alert('Chat saved successfully!');
  };

  const handleViewChat = () => {
    const saved = JSON.parse(localStorage.getItem('savedChats'));
    if (saved && Array.isArray(saved)) {
      setSavedChats(saved);
      setShowDropdown(!showDropdown);
    } else {
      alert('No saved chats found.');
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    try {
      const text = await readFileContent(file);
      const questions = extractQuestionsFromText(text);

      const answers = [];
      for (const question of questions) {
        setMessages(prev => [...prev, { text: question, isBot: false }]);
        const answer = await sendMsgToGroq(question);
        answers.push({ q: question, a: answer });
        setMessages(prev => [...prev, { text: answer, isBot: true }]);
      }

      const content = answers.map(item => `Q: ${item.q}\nA: ${item.a}`).join('\n\n');
      const blob = new Blob([content], { type: 'text/plain' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'chatbot_answers.txt';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

    } catch (err) {
      alert('Error reading file: ' + err.message);
    }
  };

  return (
    <div className="App">
      <div className="sidebar">
        <div className="upperSide">
          <div className="upperSideTop">
            <img src={chatbotlogo} alt="Logo" className="logo" />

            <button className="midBtn" onClick={() => window.location.reload()}>
              <img src={addBtn} alt="newchat" className="addBtn" />
              New Chat
            </button>

            <button className="midBtn" onClick={handleSaveChat}>
              <img src={addBtn} alt="savechat" className="addBtn" />
              Save Chat
            </button>

            <div style={{ position: "relative" }}>
              <button className="midBtn" onClick={handleViewChat}>
                <img src={addBtn} alt="viewchat" className="addBtn" />
                View Saved
              </button>

              {showDropdown && (
                <div className="dropdownMenu">
                  {savedChats.map((chat, index) => (
                    <div
                      key={index}
                      className="dropdownItem"
                      onClick={() => {
                        setMessages(chat.messages);
                        setShowDropdown(false);
                      }}
                    >
                      {chat.date}
                    </div>
                  ))}
                </div>
              )}
            </div>

            <input
              type="file"
              accept=".txt,.docx,.pdf"
              onChange={handleFileUpload}
              style={{ marginTop: "10px" }}
            />

          </div>
        </div>

        <div className="lowerSide"></div>
      </div>

      <div className="main">
        <div className="chats">
          {messages.map((message, i) => (
            <div key={i} className={`chat ${message.isBot ? "left" : "right"}`}>
              <img
                src={message.isBot ? c2clogo : usericon}
                alt={message.isBot ? "Bot" : "User"}
                className="chatIcon"
              />
              <p className="txt">{message.text}</p>
            </div>
          ))}
          <div ref={msgEnd} />
        </div>

        <div className="chatFooter">
          <div className="inp">
            <input
              type="text"
              placeholder="Send a Message"
              value={Input}
              onKeyDown={handleEnter}
              onChange={(e) => setInput(e.target.value)}
            />
            <button className="send" onClick={handleSend}>
              <img src={sendBtn} alt="Send" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
