import './App.css';
import chatbotlogo from './assets/chat.jpg';
import addBtn from './assets/addBtn.png';
import sendBtn from './assets/send.svg';
import usericon from './assets/usericon.webp';
import c2clogo from './assets/chat.jpg';
import upload from './assets/upload.png';
import { sendMsgToGroq } from './api'; // Adjust path if needed

import { useRef, useState, useEffect } from 'react';

// Replace this with your actual API call URL

function App() {
  const msgEnd = useRef(null);
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([
    {
      text: "Hi, I am Chatbot, a state-of-the-art language model. I am designed to understand and generate human-like text based on truth.",
      isBot: true,
    }
  ]);
  const [savedChats, setSavedChats] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSend = async () => {
  if (!input.trim() && !selectedFile) return;

  if (input.trim()) {
    setMessages(prev => [...prev, { text: input.trim(), isBot: false }]);
  }

  setInput('');

  try {
    let reply = "";

    if (selectedFile) {
      const formData = new FormData();
      formData.append("file", selectedFile);

      // If you also want to send the input text along with the file
      if (input.trim()) {
        formData.append("message", input.trim());
      }

      const response = await fetch("http://localhost:8000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      // Customize what you want to show from response
      reply = data.response || `File ${selectedFile.name} uploaded successfully.`;
    } else {
      reply = await sendMsgToGroq(input.trim());
    }

    setMessages(prev => [...prev, { text: reply, isBot: true }]);
  } catch (error) {
    setMessages(prev => [...prev, { text: `Error: ${error.message}`, isBot: true }]);
  } finally {
    setSelectedFile(null);
  }
};


  const handleEnter = async (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      await handleSend();
    }
  };

  const handleFileChange = (e) => {
    if (e.target.files.length > 0) {
      setSelectedFile(e.target.files[0]);
    }
  };

  // Left sidebar button handlers (unchanged)
  const handleSaveChat = () => {
    const currentDateTime = new Date().toLocaleString();
    let saved = JSON.parse(localStorage.getItem('savedChats'));
    if (!Array.isArray(saved)) saved = [];

    const newChat = {
      date: currentDateTime,
      messages: messages,
    };

    saved.push(newChat);
    localStorage.setItem('savedChats', JSON.stringify(saved));

    alert('Chat saved successfully!');
  };

  const handleViewChat = () => {
    const saved = JSON.parse(localStorage.getItem('savedChats'));
    if (saved && Array.isArray(saved)) {
      setSavedChats(saved);
      setShowDropdown(!showDropdown);
    } else {
      alert('No saved chats found.');
    }
  };

  return (
    <div className="App">
      <div className="sidebar">
        <div className="upperSide">
          <div className="upperSideTop">
            <img src={chatbotlogo} alt="Logo" className="logo" />
            <button className="midBtn" onClick={() => window.location.reload()}>
              <img src={addBtn} alt="newchat" className="addBtn" />
              New Chat
            </button>
            <button className="midBtn" onClick={handleSaveChat}>
              <img src={addBtn} alt="savechat" className="addBtn" />
              Save Chat
            </button>
            <div style={{ position: "relative" }}>
              <button className="midBtn" onClick={handleViewChat}>
                <img src={addBtn} alt="viewchat" className="addBtn" />
                View Saved
              </button>
              {showDropdown && (
                <div className="dropdownMenu">
                  {savedChats.map((chat, index) => (
                    <div
                      key={index}
                      className="dropdownItem"
                      onClick={() => {
                        setMessages(chat.messages);
                        setShowDropdown(false);
                      }}
                    >
                      {chat.date}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
        <div className="lowerSide"></div>
      </div>

      <div className="main">
        <div className="chats">
          {messages.map((message, i) => (
            <div key={i} className={`chat ${message.isBot ? "left" : "right"}`}>
              <img
                src={message.isBot ? c2clogo : usericon}
                alt={message.isBot ? "Bot" : "User"}
                className="chatIcon"
              />
              <p className="txt">{message.text}</p>
            </div>
          ))}
          <div ref={msgEnd} />
        </div>

        <div className="chatFooter">
          <div className="inp" style={{ display: "flex", alignItems: "center" }}>
            <label htmlFor="file-upload" className="file-upload-label" style={{ cursor: 'pointer', marginRight: '8px' }}>
              <img src={upload} alt="Upload File" style={{ width: '24px' }} />
            </label>
            <input
              id="file-upload"
              type="file"
              style={{ display: 'none' }}
              onChange={handleFileChange}
              accept=".txt,.pdf,.docx,.png,.jpg,.jpeg" // Accept common files, extend as needed
            />
            <input
              type="text"
              placeholder="Type your message or instruction"
              value={input}
              onKeyDown={handleEnter}
              onChange={(e) => setInput(e.target.value)}
              style={{ flexGrow: 1, marginRight: '8px' }}
            />
            <button className="send" onClick={handleSend}>
              <img src={sendBtn} alt="Send" />
            </button>
          </div>
          {selectedFile && (
            <div style={{ marginTop: '6px', fontSize: '14px', color: '#666' }}>
              Selected file: <strong>{selectedFile.name}</strong>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
