import './App.css';
import chatbotlogo from './assets/chat.jpg';
import addBtn from './assets/addBtn.png';
import sendBtn from './assets/send.svg';
import usericon from './assets/usericon.webp';
import c2clogo from './assets/chat.jpg';
import voiceIcon from './assets/voiceIcon.png';
import stopIcon from './assets/stop.png'; // NEW stop icon
import { sendMsgToGroq } from './api'

import { useRef, useState, useEffect } from 'react';

function App() {
  const msgEnd = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [voiceText, setVoiceText] = useState('');
  const [recognition, setRecognition] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioURL, setAudioURL] = useState(null);
  const [savedChats, setSavedChats] = useState([]);

  const [showDropdown, setShowDropdown] = useState(false);




  const [Input, setInput] = useState("");
  const [messages, setMessages] = useState([
    {
      text: "Hi, I am Chatbot, a state-of-the-art language model. I am designed to understand and generate human-like text based on truth.",
      isBot: true,
    }
  ]);

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognizer = new SpeechRecognition();
      recognizer.continuous = false;
      recognizer.interimResults = false;
      recognizer.lang = 'en-US';

      recognizer.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setVoiceText(transcript);
      };

      recognizer.onresult = async (event) => {
        const transcript = event.results[0][0].transcript;
        setVoiceText(transcript);

        setMessages(prev => [...prev, { text: transcript, isBot: false }]);

        const res = await sendMsgToGroq(transcript);
        setMessages(prev => [...prev, { text: res, isBot: true }]);
      };


      setRecognition(recognizer);
    }

    navigator.mediaDevices.getUserMedia({ audio: true })
      .then(stream => {
        const recorder = new MediaRecorder(stream);
        setMediaRecorder(recorder);
      })
      .catch(err => console.error('Microphone access denied', err));
  }, []);

  useEffect(() => {
    if (mediaRecorder) {
      mediaRecorder.ondataavailable = (e) => {
        const url = URL.createObjectURL(e.data);
        setAudioURL(url);
      };
    }
  }, [mediaRecorder]);

  const handleSend = async () => {
    if (Input.trim() === "") return;
    const text = Input;
    setInput('');
    setMessages(prev => [...prev, { text, isBot: false }]);

    const res = await sendMsgToGroq(text); // <-- Update here
    setMessages(prev => [...prev, { text: res, isBot: true }]);
  };

  const handleEnter = async (e) => {
    if (e.key === 'Enter') await handleSend();
  };

  const handleVoiceClick = () => {
    if (isListening) {
      recognition.stop();
      mediaRecorder?.stop();
      setIsListening(false);
    } else {
      setVoiceText('');
      setAudioURL(null);
      recognition.start();
      mediaRecorder?.start();
      setIsListening(true);
    }
  };

  // const handleSaveChat = () => {
  //   localStorage.setItem('savedChat', JSON.stringify(messages));
  //   alert('Chat saved successfully!');
  // };
  const handleSaveChat = () => {
    const currentDateTime = new Date().toLocaleString();
    let saved = JSON.parse(localStorage.getItem('savedChats'));

    if (!Array.isArray(saved)) {
      saved = [];
    }

    const newChat = {
      date: currentDateTime,
      messages: messages,
    };

    saved.push(newChat);
    localStorage.setItem('savedChats', JSON.stringify(saved));

    alert('Chat saved successfully!');
  };





  // const handleViewChat = () => {
  //   const saved = JSON.parse(localStorage.getItem('savedChat'));
  //   if (saved) {
  //     setMessages(saved);
  //   } else {
  //     alert('No saved chats found.');
  //   }
  // };
  const handleViewChat = () => {
    const saved = JSON.parse(localStorage.getItem('savedChats'));
    if (saved && Array.isArray(saved)) {
      setSavedChats(saved);
      setShowDropdown(!showDropdown); // Toggle open/close
    } else {
      alert('No saved chats found.');
    }
  };

  return (
    <div className="App">
      <div className="sidebar">
        <div className="upperSide">
          <div className="upperSideTop">
            <img src={chatbotlogo} alt="Logo" className="logo" />

            <button className="midBtn" onClick={() => window.location.reload()}>
              <img src={addBtn} alt="newchat" className="addBtn" />
              New Chat
            </button>

            <button className="midBtn" onClick={handleSaveChat}>
              <img src={addBtn} alt="savechat" className="addBtn" />
              Save Chat
            </button>

            <div style={{ position: "relative" }}>
              <button className="midBtn" onClick={handleViewChat}>
                <img src={addBtn} alt="viewchat" className="addBtn" />
                View Saved
              </button>

              {showDropdown && (
                <div className="dropdownMenu">
                  {savedChats.map((chat, index) => (
                    <div
                      key={index}
                      className="dropdownItem"
                      onClick={() => {
                        setMessages(chat.messages);
                        setShowDropdown(false);
                      }}
                    >
                      {chat.date}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="lowerSide"></div>
      </div>

      <div className="main">
        <div className="chats">
          {messages.map((message, i) => (
            <div key={i} className={`chat ${message.isBot ? "left" : "right"}`}>
              <img
                src={message.isBot ? c2clogo : usericon}
                alt={message.isBot ? "Bot" : "User"}
                className="chatIcon"
              />
              <p className="txt">{message.text}</p>
            </div>
          ))}

          {audioURL && (
            <div className="chat right">
              <img
                src={usericon}
                alt="User"
                className="chatIcon"
              />
              <div>
                <audio controls src={audioURL} style={{ marginBottom: "5px" }} />
                <p className="txt">{voiceText}</p>
              </div>
            </div>
          )}

          <div ref={msgEnd} />
        </div>

        <div className="chatFooter">
          <div className="inp">
            <input
              type="text"
              placeholder="Send a Message"
              value={Input}
              onKeyDown={handleEnter}
              onChange={(e) => setInput(e.target.value)}
            />
            <button className="send" onClick={handleSend}>
              <img src={sendBtn} alt="Send" />
            </button>
            <button className="voiceBtn" onClick={handleVoiceClick}>
              <img
                src={isListening ? stopIcon : voiceIcon}
                alt="Voice Input"
                className="voiceIcon"
              />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
export default App;
