import './App.css';
import chatbotlogo from './assets/chat.jpg';
import addBtn from './assets/addBtn.png';
import sendBtn from './assets/send.svg';
import usericon from './assets/usericon.webp';
import c2clogo from './assets/chat.jpg';
import voiceIcon from './assets/voiceIcon.png';
import { sendMsgToOpenAI } from './api';
import { useRef, useState, useEffect } from 'react';

function App() {
  const msgEnd = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [voiceText, setVoiceText] = useState('');
  const [recognition, setRecognition] = useState(null);

  const [Input, setInput] = useState("");
  const [messages, setMessages] = useState([
    {
      text: "Hi, I am Chatbot, a state-of-the-art language model. I am designed to understand and generate human-like text based on truth.",
      isBot: true,
    }
  ]);

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSend = async () => {
    if (Input.trim() === "") return; 
    const text = Input;
    setInput('');
    setMessages(prev => [
      ...prev,
      { text, isBot: false }
    ]);

    const res = await sendMsgToOpenAI(text);
    setMessages(prev => [
      ...prev,
      { text: res, isBot: true }
    ]);
  }

  const handleEnter = async (e) => {
    if (e.key === 'Enter') await handleSend();
  }


  const handleVoiceClick = () => {
    if (isListening) {
      recognition.stop();
      setIsListening(false);
    } else {
      recognition.start();
      setIsListening(true);
    }
  };

  useEffect(() => {

    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognizer = new SpeechRecognition();
      recognizer.continuous = false;
      recognizer.interimResults = false;
      recognizer.lang = 'en-US';

      recognizer.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setVoiceText(transcript);
      };

      recognizer.onerror = (event) => {
        console.error('Voice recognition error', event.error);
      };

      setRecognition(recognizer);
    }
  }, []);

  return (
    <div className="App">
      <div className="sidebar">
        <div className="upperSide">
          <div className="upperSideTop">
            <img src={chatbotlogo} alt="Logo" className="logo" />
            <button className="midBtn" onClick={() => window.location.reload()}>
              <img src={addBtn} alt="newchat" className="addBtn" />
              New Chat
            </button>
          </div>
        </div>
        <div className="lowerSide"> </div>
      </div>

      <div className="main">
        <div className="chats">
          {messages.map((message, i) => (
            <div key={i} className="chat">
              <img
                src={message.isBot ? c2clogo : usericon}
                alt={message.isBot ? "Bot" : "User"}
                className="chatIcon"
                style={{ width: "40px", height: "40px", marginRight: "10px", borderRadius: "50%" }}
              />
              <p className="txt">{message.text}</p>
            </div>
          ))}
          <div ref={msgEnd} />
        </div>

        <div className="chatFooter">
          <div className="inp">
            <input
              type="text"
              placeholder="Send a Message"
              value={Input}
              onKeyDown={handleEnter}
              onChange={(e) => setInput(e.target.value)}
            />
            <button className="send" onClick={handleSend}>
              <img src={sendBtn} alt="Send" />
            </button>
            <button className="voiceBtn" onClick={handleVoiceClick}>
            <img src={voiceIcon} alt="Voice Input" className="voiceIcon" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
