import './App.css';
import chatbotlogo from './assets/chat.jpg';
import addBtn from './assets/addBtn.png';
import sendBtn from './assets/send.svg';
import usericon from './assets/usericon.webp';
import c2clogo from './assets/chat.jpg';
import { sendMsgToGroq } from './api';

import { useRef, useState, useEffect } from 'react';

function App() {
  const msgEnd = useRef(null);
  const [Input, setInput] = useState("");
  const [messages, setMessages] = useState([
    {
      text: "Hi, I am Chatbot, a state-of-the-art language model. I am designed to understand and generate human-like text based on truth.",
      isBot: true,
      isFile: false,
      filename: null,
    }
  ]);
  const [savedChats, setSavedChats] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Handle text message send
  const handleSend = async () => {
    if (Input.trim() === "" && !selectedFile) return;

    if (selectedFile) {
      // Send file + optional text message (Input) to backend
      const fileName = selectedFile.name;
      setMessages(prev => [...prev, { text: fileName, isBot: false, isFile: true, filename: fileName }]);

      // Prepare file to send
      const fileText = await readFileContent(selectedFile);

      // You can send file text + optional Input to your backend API
      const res = await sendMsgToGroq("", fileText); // Assuming API supports file text param

      setMessages(prev => [...prev, { text: res, isBot: true, isFile: false, filename: null }]);

      setSelectedFile(null);
      setInput("");
    } else {
      // Normal text message
      const text = Input;
      setInput('');
      setMessages(prev => [...prev, { text, isBot: false, isFile: false, filename: null }]);

      const res = await sendMsgToGroq(text);
      setMessages(prev => [...prev, { text: res, isBot: true, isFile: false, filename: null }]);
    }
  };

  // Read file content as text (for txt files)
  // For pdf/docx, ideally do backend processing or use libraries
  const readFileContent = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (e) => resolve(e.target.result);
      reader.onerror = () => reject("File reading error");

      // For simplicity, reading as text. For pdf/docx you should send file itself to backend.
      reader.readAsText(file);
    });
  };

  const handleEnter = async (e) => {
    if (e.key === 'Enter') await handleSend();
  };

  // File input change handler
  const handleFileChange = (e) => {
    if (e.target.files.length > 0) {
      const file = e.target.files[0];
      setSelectedFile(file);
    }
  };

  const handleSaveChat = () => {
    const currentDateTime = new Date().toLocaleString();
    let saved = JSON.parse(localStorage.getItem('savedChats'));
    if (!Array.isArray(saved)) saved = [];

    const newChat = {
      date: currentDateTime,
      messages: messages,
    };

    saved.push(newChat);
    localStorage.setItem('savedChats', JSON.stringify(saved));

    alert('Chat saved successfully!');
  };

  const handleViewChat = () => {
    const saved = JSON.parse(localStorage.getItem('savedChats'));
    if (saved && Array.isArray(saved)) {
      setSavedChats(saved);
      setShowDropdown(!showDropdown);
    } else {
      alert('No saved chats found.');
    }
  };

  // Download chat answers only (bot messages) as txt file
  const handleDownloadAnswers = () => {
    const answers = messages.filter(m => m.isBot).map(m => m.text).join("\n\n");
    const blob = new Blob([answers], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'chatbot_answers.txt';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="App">
      <div className="sidebar">
        <div className="upperSide">
          <div className="upperSideTop">
            <img src={chatbotlogo} alt="Logo" className="logo" />
            <button className="midBtn" onClick={() => window.location.reload()}>
              <img src={addBtn} alt="newchat" className="addBtn" />
              New Chat
            </button>
            <button className="midBtn" onClick={handleSaveChat}>
              <img src={addBtn} alt="savechat" className="addBtn" />
              Save Chat
            </button>
            <button className="midBtn" onClick={handleDownloadAnswers}>
              <img src={addBtn} alt="download" className="addBtn" />
              Download Answers
            </button>
            <div style={{ position: "relative" }}>
              <button className="midBtn" onClick={handleViewChat}>
                <img src={addBtn} alt="viewchat" className="addBtn" />
                View Saved
              </button>
              {showDropdown && (
                <div className="dropdownMenu">
                  {savedChats.map((chat, index) => (
                    <div
                      key={index}
                      className="dropdownItem"
                      onClick={() => {
                        setMessages(chat.messages);
                        setShowDropdown(false);
                      }}
                    >
                      {chat.date}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
        <div className="lowerSide"></div>
      </div>

      <div className="main">
        <div className="chats">
          {messages.map((message, i) => (
            <div key={i} className={`chat ${message.isBot ? "left" : "right"}`}>
              <img
                src={message.isBot ? c2clogo : usericon}
                alt={message.isBot ? "Bot" : "User"}
                className="chatIcon"
              />
              {message.isFile ? (
                <p className="txt fileMsg">
                  üìé <strong>{message.filename}</strong>
                </p>
              ) : (
                <p className="txt">{message.text}</p>
              )}
            </div>
          ))}
          <div ref={msgEnd} />
        </div>

        <div className="chatFooter">
          <div className="inp">
            {/* Upload Button */}
            <label htmlFor="file-upload" className="uploadBtn" title="Upload File">
              üìÅ
            </label>
            <input
              id="file-upload"
              type="file"
              accept=".txt,.pdf,.docx"
              style={{ display: "none" }}
              onChange={handleFileChange}
            />

            <input
              type="text"
              placeholder={selectedFile ? `Ready to send ${selectedFile.name}` : "Send a Message"}
              value={Input}
              onKeyDown={handleEnter}
              onChange={(e) => setInput(e.target.value)}
              disabled={!!selectedFile}
            />
            <button className="send" onClick={handleSend}>
              <img src={sendBtn} alt="Send" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
