import './App.css';
import chatbotlogo from './assets/chat.jpg';
import addBtn from './assets/addBtn.png';
import sendBtn from './assets/send.svg';
import usericon from './assets/usericon.webp';
import c2clogo from './assets/chat.jpg';
import upload from './assets/upload.png'; // Your upload icon

import { useRef, useState, useEffect } from 'react';
import { sendMsgToGroq } from './api'; // Your API call function

function App() {
  const msgEnd = useRef(null);
  const [Input, setInput] = useState("");
  const [messages, setMessages] = useState([
    {
      text: "Hi, I am Chatbot, a state-of-the-art language model. I am designed to understand and generate human-like text based on truth.",
      isBot: true,
    }
  ]);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Handle sending both file + message
  const handleSendWithFile = async (file, message) => {
    if (!file && message.trim() === "") return;

    const formData = new FormData();
    if (file) formData.append("file", file);
    formData.append("message", message.trim());

    // Show sent message in chat
    const fileText = file ? `[File: ${file.name}]` : "";
    setMessages(prev => [...prev, { text: `${fileText} ${message}`, isBot: false }]);
    setInput("");
    setSelectedFile(null);

    try {
      const res = await sendMsgToGroq(formData);
      setMessages(prev => [...prev, { text: res, isBot: true }]);
    } catch (error) {
      alert("Error sending message or file.");
    }
  };

  const handleSend = async () => {
    if (Input.trim() === "" && !selectedFile) return;
    await handleSendWithFile(selectedFile, Input);
  };

  const handleEnter = async (e) => {
    if (e.key === 'Enter') {
      e.preventDefault(); // prevent newline in input
      await handleSend();
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) setSelectedFile(file);
  };

  return (
    <div className="App">
      <div className="sidebar">
        <div className="upperSide">
          <div className="upperSideTop">
            <img src={chatbotlogo} alt="Logo" className="logo" />
            <button className="midBtn" onClick={() => window.location.reload()}>
              <img src={addBtn} alt="newchat" className="addBtn" />
              New Chat
            </button>
            {/* Add more sidebar buttons here if needed */}
          </div>
        </div>
        <div className="lowerSide"></div>
      </div>

      <div className="main">
        <div className="chats">
          {messages.map((message, i) => (
            <div key={i} className={`chat ${message.isBot ? "left" : "right"}`}>
              <img
                src={message.isBot ? c2clogo : usericon}
                alt={message.isBot ? "Bot" : "User"}
                className="chatIcon"
              />
              <p className="txt">{message.text}</p>
            </div>
          ))}
          <div ref={msgEnd} />
        </div>

        <div className="chatFooter" style={{ display: "flex", alignItems: "center", padding: '10px' }}>
          <label htmlFor="file-upload" style={{ cursor: "pointer", marginRight: "8px" }}>
            <img src={upload} alt="Upload File" style={{ width: '24px' }} />
          </label>
          <input
            id="file-upload"
            type="file"
            style={{ display: 'none' }}
            onChange={handleFileChange}
          />

          <input
            type="text"
            placeholder="Type your message or instruction"
            value={Input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleEnter}
            style={{ flexGrow: 1, marginRight: '8px', padding: '6px 10px', fontSize: '1rem' }}
          />

          <button className="send" onClick={handleSend} style={{ border: 'none', background: 'transparent', cursor: 'pointer' }}>
            <img src={sendBtn} alt="Send" />
          </button>
        </div>

        {selectedFile && (
          <div style={{ padding: '0 10px 10px', fontStyle: 'italic', fontSize: '0.9rem', color: '#555' }}>
            Selected file: {selectedFile.name}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
