import './App.css';
import chatbotlogo from './assets/chat.jpg';
import addBtn from './assets/addBtn.png';
import sendBtn from './assets/send.svg';
import usericon from './assets/usericon.webp';
import c2clogo from './assets/chat.jpg';
import upload from './assets/upload.png'; // Your upload icon

import { sendMsgToGroq } from './api';

import { useRef, useState, useEffect } from 'react';
import mammoth from "mammoth";
import * as pdfjsLib from "pdfjs-dist";

const extractTextFromFile = async (file) => {
  const ext = file.name.split('.').pop().toLowerCase();

  if (ext === "txt") {
    return await readTextFile(file);
  } else if (ext === "docx") {
    return await readDocxFile(file);
  } else if (ext === "pdf") {
    return await readPdfFile(file);
  } else {
    alert("Unsupported file type.");
    return "";
  }
};

const readTextFile = (file) => {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = () => resolve(reader.result);
    reader.onerror = reject;
    reader.readAsText(file);
  });
};

const readDocxFile = async (file) => {
  const arrayBuffer = await file.arrayBuffer();
  const result = await mammoth.extractRawText({ arrayBuffer });
  return result.value;
};

const readPdfFile = async (file) => {
  const arrayBuffer = await file.arrayBuffer();
  const pdf = await pdfjsLib.getDocument({ data: arrayBuffer }).promise;
  let text = '';
  for (let i = 1; i <= pdf.numPages; i++) {
    const page = await pdf.getPage(i);
    const content = await page.getTextContent();
    text += content.items.map((item) => item.str).join(' ') + '\n';
  }
  return text;
};

function App() {
  const msgEnd = useRef(null);
  const [Input, setInput] = useState("");
  const [messages, setMessages] = useState([
    {
      text: "Hi, I am Chatbot, a state-of-the-art language model. I am designed to understand and generate human-like text based on truth.",
      isBot: true,
    }
  ]);
  const [savedChats, setSavedChats] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSend = async () => {
    if (Input.trim() === "") return;
    const text = Input;
    setInput('');
    setMessages(prev => [...prev, { text, isBot: false }]);

    const res = await sendMsgToGroq(text);
    setMessages(prev => [...prev, { text: res, isBot: true }]);
  };

  const handleEnter = async (e) => {
    if (e.key === 'Enter') await handleSend();
  };

  const handleSaveChat = () => {
    const currentDateTime = new Date().toLocaleString();
    let saved = JSON.parse(localStorage.getItem('savedChats'));
    if (!Array.isArray(saved)) saved = [];

    const newChat = {
      date: currentDateTime,
      messages: messages,
    };

    saved.push(newChat);
    localStorage.setItem('savedChats', JSON.stringify(saved));

    alert('Chat saved successfully!');
  };

  const handleViewChat = () => {
    const saved = JSON.parse(localStorage.getItem('savedChats'));
    if (saved && Array.isArray(saved)) {
      setSavedChats(saved);
      setShowDropdown(!showDropdown);
    } else {
      alert('No saved chats found.');
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const fileText = await extractTextFromFile(file);

    // Combine typed message + file content
    const combinedPrompt = `${Input.trim()}\n\n${fileText}`;
    setMessages(prev => [...prev, { text: combinedPrompt, isBot: false }]);
    setInput('');

    const res = await sendMsgToGroq(combinedPrompt);
    setMessages(prev => [...prev, { text: res, isBot: true }]);

    const action = window.prompt("Type 'save' to download the answer, or 'view' to just display:");

    if (action === "save") {
      const blob = new Blob([`Q: ${combinedPrompt}\nA: ${res}`], { type: "text/plain" });
      const link = document.createElement("a");
      link.href = URL.createObjectURL(blob);
      link.download = "file_with_reason_answer.txt";
      link.click();
    }
  };

  return (
    <div className="App">
      <div className="sidebar">
        <div className="upperSide">
          <div className="upperSideTop">
            <img src={chatbotlogo} alt="Logo" className="logo" />
            <button className="midBtn" onClick={() => window.location.reload()}>
              <img src={addBtn} alt="newchat" className="addBtn" />
              New Chat
            </button>
            <button className="midBtn" onClick={handleSaveChat}>
              <img src={addBtn} alt="savechat" className="addBtn" />
              Save Chat
            </button>
            <div style={{ position: "relative" }}>
              <button className="midBtn" onClick={handleViewChat}>
                <img src={addBtn} alt="viewchat" className="addBtn" />
                View Saved
              </button>
              {showDropdown && (
                <div className="dropdownMenu">
                  {savedChats.map((chat, index) => (
                    <div
                      key={index}
                      className="dropdownItem"
                      onClick={() => {
                        setMessages(chat.messages);
                        setShowDropdown(false);
                      }}
                    >
                      {chat.date}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
        <div className="lowerSide"></div>
      </div>

      <div className="main">
        <div className="chats">
          {messages.map((message, i) => (
            <div key={i} className={`chat ${message.isBot ? "left" : "right"}`}>
              <img
                src={message.isBot ? c2clogo : usericon}
                alt={message.isBot ? "Bot" : "User"}
                className="chatIcon"
              />
              <p className="txt">{message.text}</p>
            </div>
          ))}
          <div ref={msgEnd} />
        </div>

        {/* Chat footer with upload icon, input box, and send button */}
        <div className="chatFooter">
          <div className="inp" style={{ display: "flex", alignItems: "center" }}>
            {/* Upload file icon triggers hidden input */}
            <label htmlFor="file-upload" className="file-upload-label" style={{ marginRight: '8px' }}>
              <img src={upload} alt="Upload File" style={{ width: '24px', cursor: 'pointer' }} />
            </label>
            <input
              id="file-upload"
              type="file"
              accept=".txt,.pdf,.docx"
              style={{ display: 'none' }}
              onChange={handleFileUpload}
            />

            {/* Text input for message or reason */}
            <input
              type="text"
              placeholder="Type your message or instruction"
              value={Input}
              onKeyDown={handleEnter}
              onChange={(e) => setInput(e.target.value)}
              style={{ flexGrow: 1, marginRight: '8px' }}
            />

            {/* Send button */}
            <button className="send" onClick={handleSend}>
              <img src={sendBtn} alt="Send" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
