import axios from 'axios';

const API_URL = "http://127.0.0.1:8000/chat"; // Your FastAPI backend

export const sendMsgToGroq = async (message) => {
  try {
    const response = await axios.post(API_URL, { message });

    if (response.data && response.data.reply) {
      return {
        reply: response.data.reply,
        audioUrl: `http://127.0.0.1:8000${response.data.audio_url}`
      };
    } else {
      return {
        reply: "No valid response from server.",
        audioUrl: null
      };
    }
  } catch (error) {
    console.error("Error from API:", error);
    return {
      reply: "Error communicating with server.",
      audioUrl: null
    };
  }
};
