// 
import './App.css';
import chatbotlogo from './assets/chat.jpg';
import voiceIcon from './assets/voiceIcon.png';
import stopIcon from './assets/stop.png';
import usericon from './assets/usericon.webp';
import c2clogo from './assets/chat.jpg';
import { useEffect, useRef, useState } from 'react';
import axios from 'axios';

const ASSEMBLY_API = 'ae11b573d3764c55bf97dc3115f6f781';
const BACKEND_URL = 'http://127.0.0.1:8000/chat';

function App() {
  const msgEnd = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioChunks, setAudioChunks] = useState([]);
  const [audioURL, setAudioURL] = useState(null);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    msgEnd.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then(stream => {
        const recorder = new MediaRecorder(stream);
        recorder.ondataavailable = (e) => setAudioChunks(prev => [...prev, e.data]);
        setMediaRecorder(recorder);
      })
      .catch(err => console.error('Microphone access denied:', err));
  }, []);

  const handleVoiceClick = async () => {
    if (isListening) {
      mediaRecorder?.stop();
      setIsListening(false);
    } else {
      setAudioChunks([]);
      mediaRecorder?.start();
      setIsListening(true);
    }
  };

  useEffect(() => {
    if (mediaRecorder) {
      mediaRecorder.onstop = async () => {
        const blob = new Blob(audioChunks, { type: 'audio/webm' });
        const url = URL.createObjectURL(blob);
        setAudioURL(url);
        const transcript = await transcribeAudio(blob);
        if (transcript) {
          const botReply = await fetchBotResponse(transcript);
          speak(botReply);
          setMessages(prev => [...prev, { text: transcript, isBot: false }, { text: botReply, isBot: true }]);
        }
      };
    }
  }, [mediaRecorder, audioChunks]);

  const transcribeAudio = async (blob) => {
    const uploadRes = await axios.post('https://api.assemblyai.com/v2/upload', blob, {
      headers: {
        'authorization': ASSEMBLY_API,
        'content-type': 'application/octet-stream'
      }
    });

    const transcriptRes = await axios.post('https://api.assemblyai.com/v2/transcript', {
      audio_url: uploadRes.data.upload_url
    }, {
      headers: { authorization: ASSEMBLY_API }
    });

    const transcriptId = transcriptRes.data.id;
    let completedTranscript = null;

    while (true) {
      const pollingRes = await axios.get(`https://api.assemblyai.com/v2/transcript/${transcriptId}`, {
        headers: { authorization: ASSEMBLY_API }
      });

      if (pollingRes.data.status === 'completed') {
        completedTranscript = pollingRes.data.text;
        break;
      } else if (pollingRes.data.status === 'failed') {
        console.error('Transcription failed');
        break;
      }
      await new Promise(res => setTimeout(res, 2000));
    }

    return completedTranscript;
  };

  const fetchBotResponse = async (text) => {
    const res = await axios.post(BACKEND_URL, { message: text });
    return res.data.reply;
  };

  const speak = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    window.speechSynthesis.speak(utterance);
  };

  return (
    <div className="App">
      <div className="sidebar">
        <div className="upperSideTop">
          <img src={chatbotlogo} alt="Logo" className="logo" />
        </div>
      </div>
      <div className="main">
        <div className="chats">
          {messages.map((message, i) => (
            <div key={i} className={`chat ${message.isBot ? "left" : "right"}`}>
              <img src={message.isBot ? c2clogo : usericon} alt={message.isBot ? "Bot" : "User"} className="chatIcon" />
              <p className="txt">{message.text}</p>
            </div>
          ))}
          {audioURL && (
            <div className="chat right">
              <audio controls src={audioURL} style={{ marginBottom: "5px" }} />
            </div>
          )}
          <div ref={msgEnd} />
        </div>
        <div className="chatFooter">
          <button className="voiceBtn" onClick={handleVoiceClick}>
            <img src={isListening ? stopIcon : voiceIcon} alt="Voice Input" className="voiceIcon" />
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
